svg.script:
//!export-start
let _pn_state = 0;  // Button state: 0=off, 1=on
//!export-end

function init() {
  update();
}

function update() {
  const btnRect = document.getElementById('btnRect');
  const btnText = document.getElementById('btnText');
  const statusLED = document.getElementById('statusLED');

  if (!btnRect) return;

  if (_pn_state) {
    btnRect.classList.add('active');
    btnText.classList.add('active');
    btnText.textContent = 'ON';
    statusLED.setAttribute('fill', '#4CAF50');
  } else {
    btnRect.classList.remove('active');
    btnText.classList.remove('active');
    btnText.textContent = 'OFF';
    statusLED.setAttribute('fill', '#999');
  }
}

function putValue(id, value) {
  if (id === '_pn_state') {
    _pn_state = Number(value) || 0;
  }

  update();
}

init();
