svg.script:
(function() {
  // FUXA Integration Variables
  var isRunning = false;
  var tagId = ''; // Set by FUXA: variable binding for run status
  var cmdTagId = ''; // Set by FUXA: variable binding for start/stop command

  // DOM Elements
  var btnRect = document.getElementById('btnRect');
  var btnText = document.getElementById('btnText');
  var statusLED = document.getElementById('statusLED');
  var btnGroup = document.getElementById('btnGroup');

  // Update visual state
  function updateVisuals() {
    if (isRunning) {
      btnRect.classList.add('running');
      btnText.textContent = 'STOP';
      statusLED.setAttribute('fill', '#4CAF50');
      statusLED.classList.add('running');
    } else {
      btnRect.classList.remove('running');
      btnText.textContent = 'START';
      statusLED.setAttribute('fill', '#999');
      statusLED.classList.remove('running');
    }
  }

  // Click handler - momentary action
  btnGroup.addEventListener('mousedown', function(evt) {
    evt.preventDefault();

    // Send momentary pulse command
    if (typeof GAuxy !== 'undefined' && cmdTagId) {
      var command = isRunning ? 0 : 1; // 1 for START, 0 for STOP
      GAuxy.setVariable(cmdTagId, command);

      // Reset command after 200ms (momentary)
      setTimeout(function() {
        if (typeof GAuxy !== 'undefined' && cmdTagId) {
          GAuxy.setVariable(cmdTagId, -1); // Reset command
        }
      }, 200);
    }
  });

  // FUXA Integration Functions
  window.svgSetValue = function(sigid, value) {
    if (sigid === tagId) {
      isRunning = (value > 0);
      updateVisuals();
    }
  };

  window.svgGetProperty = function(name) {
    if (name === 'variableId') return tagId;
    if (name === 'cmdVariableId') return cmdTagId;
    return null;
  };

  window.svgSetProperty = function(name, value) {
    if (name === 'variableId') {
      tagId = value;
    } else if (name === 'cmdVariableId') {
      cmdTagId = value;
    }
  };

  // Initialize
  updateVisuals();
})();
