svg.script:

// Exported variables for FUXA
var supplyFanSpeed = 65; // 0-100%
var returnFanSpeed = 60; // 0-100%
var supplyFanStatus = true;
var returnFanStatus = true;
var coolingValve = 0; // 0-100%
var heatingValve = 25; // 0-100%
var returnTemp = 72; // degrees F
var supplyTemp = 58; // degrees F
var filterDirty = false;
var alarmActive = false;

function updateDisplay() {
  // Update temperatures
  document.getElementById('returnTemp').textContent = returnTemp.toFixed(0) + '째F';
  document.getElementById('supplyTemp').textContent = supplyTemp.toFixed(0) + '째F';

  // Update supply fan
  var sfText = document.getElementById('supplyFanStatus');
  if (supplyFanStatus) {
    sfText.textContent = 'ON - ' + supplyFanSpeed.toFixed(0) + '%';
    sfText.style.fill = '#27ae60';
    document.getElementById('supplyFan').querySelector('.ahu-fan-blade').classList.add('ahu-fan-active');
  } else {
    sfText.textContent = 'OFF';
    sfText.style.fill = '#7f8c8d';
    document.getElementById('supplyFan').querySelector('.ahu-fan-blade').classList.remove('ahu-fan-active');
  }

  // Update return fan
  var rfText = document.getElementById('returnFanStatus');
  if (returnFanStatus) {
    rfText.textContent = 'ON - ' + returnFanSpeed.toFixed(0) + '%';
    rfText.style.fill = '#27ae60';
    document.getElementById('returnFan').querySelector('.ahu-fan-blade').classList.add('ahu-fan-active');
  } else {
    rfText.textContent = 'OFF';
    rfText.style.fill = '#7f8c8d';
    document.getElementById('returnFan').querySelector('.ahu-fan-blade').classList.remove('ahu-fan-active');
  }

  // Update cooling coil
  document.getElementById('coolingValve').textContent = coolingValve.toFixed(0) + '%';
  var ccCoil = document.getElementById('coolingCoil');
  if (coolingValve > 5) {
    ccCoil.classList.add('ahu-coil-active');
  } else {
    ccCoil.classList.remove('ahu-coil-active');
  }

  // Update heating coil
  document.getElementById('heatingValve').textContent = heatingValve.toFixed(0) + '%';
  var hcCoil = document.getElementById('heatingCoil');
  if (heatingValve > 5) {
    hcCoil.classList.add('ahu-coil-active');
  } else {
    hcCoil.classList.remove('ahu-coil-active');
  }

  // Update filter status
  var filterLight = document.getElementById('filterLight');
  var filterText = document.getElementById('filterText');
  if (filterDirty) {
    filterLight.setAttribute('class', 'status-alarm');
    filterText.textContent = 'Dirty';
    filterText.style.fill = '#e74c3c';
  } else {
    filterLight.setAttribute('class', 'status-ok');
    filterText.textContent = 'Clean';
    filterText.style.fill = '#27ae60';
  }

  // Update system status
  var statusLight = document.getElementById('statusLight');
  var statusText = document.getElementById('statusText');
  if (alarmActive) {
    statusLight.setAttribute('class', 'status-alarm');
    statusText.textContent = 'Alarm';
    statusText.style.fill = '#e74c3c';
  } else {
    statusLight.setAttribute('class', 'status-ok');
    statusText.textContent = 'Normal';
    statusText.style.fill = '#27ae60';
  }
}

// Initialize display
updateDisplay();

// Update display every 100ms
setInterval(updateDisplay, 100);
