svg.script:

// Exported variables for FUXA
var fanStatus = false; // On/Off
var fanSpeed = 0; // 0-100%
var airflow = 0; // CFM
var damperPosition = 0; // 0-100%
var alarmActive = false;

function updateDisplay() {
  // Update status indicator
  var statusLight = document.getElementById('statusLight');
  var statusText = document.getElementById('statusText');

  if (alarmActive) {
    statusLight.setAttribute('class', 'status-alarm');
    statusText.textContent = 'Alarm';
    statusText.style.fill = '#e74c3c';
  } else if (fanStatus) {
    statusLight.setAttribute('class', 'status-running');
    statusText.textContent = 'Running';
    statusText.style.fill = '#27ae60';
  } else {
    statusLight.setAttribute('class', 'status-ok');
    statusText.textContent = 'Off';
    statusText.style.fill = '#7f8c8d';
  }

  // Update speed
  document.getElementById('speed').textContent = fanSpeed.toFixed(0) + '%';

  // Update airflow (proportional to speed)
  if (fanStatus) {
    airflow = (fanSpeed / 100) * 2000; // Max 2000 CFM
  } else {
    airflow = 0;
  }
  document.getElementById('airflow').textContent = airflow.toFixed(0) + ' CFM';

  // Update damper position
  document.getElementById('damperPos').textContent = damperPosition.toFixed(0) + '%';

  // Rotate damper visual
  var damperAngle = (damperPosition / 100) * 90;
  document.getElementById('damper').setAttribute('transform',
    'translate(140, 110) rotate(' + damperAngle + ')');

  // Fan animation
  var fanBlade = document.getElementById('fan').querySelector('.exh-fan-blade');
  if (fanStatus && fanSpeed > 0) {
    fanBlade.classList.add('exh-fan-active');
    var animSpeed = 2.0 / (fanSpeed / 100); // Faster for higher speeds
    fanBlade.style.animationDuration = Math.max(0.3, animSpeed) + 's';
  } else {
    fanBlade.classList.remove('exh-fan-active');
  }

  // Show/hide flow arrows
  var flowArrows = document.getElementById('flowArrows');
  if (fanStatus && airflow > 100) {
    flowArrows.style.display = 'block';
  } else {
    flowArrows.style.display = 'none';
  }

  // Auto control damper with fan
  if (fanStatus) {
    damperPosition = Math.max(damperPosition, 50); // Open damper when fan is on
  }
}

// Initialize display
updateDisplay();

// Update display every 100ms
setInterval(updateDisplay, 100);
