svg.script:

// Exported variables for FUXA
var fanSpeed = 0; // 0=Off, 1=Low, 2=Med, 3=High, 4=Auto
var roomTemp = 72.5; // degrees F
var setpoint = 72.0; // degrees F
var valvePosition = 45; // 0-100%
var enabled = true;
var alarmActive = false;

function updateDisplay() {
  // Update temperatures
  document.getElementById('roomTemp').textContent = roomTemp.toFixed(1) + '째F';
  document.getElementById('setpoint').textContent = setpoint.toFixed(1) + '째F';

  // Update valve position
  document.getElementById('valvePos').textContent = valvePosition.toFixed(0) + '%';

  // Update fan speed buttons
  document.getElementById('speedOff').querySelector('rect').classList.remove('fcu-btn-active');
  document.getElementById('speedLow').querySelector('rect').classList.remove('fcu-btn-active');
  document.getElementById('speedMed').querySelector('rect').classList.remove('fcu-btn-active');
  document.getElementById('speedHigh').querySelector('rect').classList.remove('fcu-btn-active');
  document.getElementById('speedAuto').querySelector('rect').classList.remove('fcu-btn-active');

  var fanSpeedNames = ['Off', 'Low', 'Med', 'High', 'Auto'];
  document.getElementById('fanSpeedText').textContent = fanSpeedNames[fanSpeed];

  if (fanSpeed === 0) document.getElementById('speedOff').querySelector('rect').classList.add('fcu-btn-active');
  else if (fanSpeed === 1) document.getElementById('speedLow').querySelector('rect').classList.add('fcu-btn-active');
  else if (fanSpeed === 2) document.getElementById('speedMed').querySelector('rect').classList.add('fcu-btn-active');
  else if (fanSpeed === 3) document.getElementById('speedHigh').querySelector('rect').classList.add('fcu-btn-active');
  else if (fanSpeed === 4) document.getElementById('speedAuto').querySelector('rect').classList.add('fcu-btn-active');

  // Fan animation
  var fanBlade = document.getElementById('fan').querySelector('.fcu-fan-blade');
  if (fanSpeed > 0) {
    fanBlade.classList.add('fcu-fan-active');
    var animSpeed = 2.0 / fanSpeed; // Faster for higher speeds
    fanBlade.style.animationDuration = animSpeed + 's';
  } else {
    fanBlade.classList.remove('fcu-fan-active');
  }

  // Coil animation
  var coil = document.getElementById('coil');
  if (valvePosition > 5) {
    coil.classList.add('fcu-coil-active');
  } else {
    coil.classList.remove('fcu-coil-active');
  }

  // Auto mode logic
  if (fanSpeed === 4) { // Auto mode
    var tempError = Math.abs(roomTemp - setpoint);
    if (tempError < 0.5) {
      // Close to setpoint, low speed
      valvePosition = 25;
    } else if (tempError < 2) {
      // Moderate error, medium speed
      valvePosition = 50;
    } else {
      // Large error, high speed
      valvePosition = 75;
    }
  }

  // Update status
  var statusLight = document.getElementById('statusLight');
  var statusText = document.getElementById('statusText');

  if (!enabled) {
    statusLight.setAttribute('class', 'status-alarm');
    statusText.textContent = 'Disabled';
    statusText.style.fill = '#e74c3c';
  } else if (alarmActive) {
    statusLight.setAttribute('class', 'status-alarm');
    statusText.textContent = 'Alarm';
    statusText.style.fill = '#e74c3c';
  } else {
    statusLight.setAttribute('class', 'status-ok');
    statusText.textContent = 'Normal';
    statusText.style.fill = '#27ae60';
  }
}

function setFanSpeed(speed) {
  fanSpeed = speed;
  updateDisplay();
}

// Initialize display
updateDisplay();

// Update display every 100ms
setInterval(updateDisplay, 100);
