svg.script:

// Exported variables for FUXA
var supplyFanSpeed = 75; // 0-100%
var supplyFanStatus = true;
var economizerPosition = 25; // 0-100%
var compressor1 = false;
var compressor2 = false;
var heatingActive = false;
var heatingOutput = 0; // 0-100%
var oaTemp = 45; // degrees F
var raTemp = 72; // degrees F
var saTemp = 58; // degrees F
var filterDirty = false;
var alarmActive = false;
var coolingStage = 0; // 0=Off, 1=Stage1, 2=Stage2
var heatingStage = 0; // 0=Off, 1=Stage1, 2=Stage2

function updateDisplay() {
  // Update temperatures
  document.getElementById('oaTemp').textContent = oaTemp.toFixed(0) + '°F';
  document.getElementById('raTemp').textContent = raTemp.toFixed(0) + '°F';
  document.getElementById('saTemp').textContent = saTemp.toFixed(0) + '°F';
  document.getElementById('oaTempData').textContent = oaTemp.toFixed(0) + '°F';
  document.getElementById('raTempData').textContent = raTemp.toFixed(0) + '°F';
  document.getElementById('saTempData').textContent = saTemp.toFixed(0) + '°F';

  // Update supply fan
  var sfText = document.getElementById('sfStatus');
  if (supplyFanStatus) {
    sfText.textContent = 'ON - ' + supplyFanSpeed.toFixed(0) + '%';
    sfText.style.fill = '#27ae60';
    document.getElementById('supplyFan').querySelector('.rtu-fan-blade').classList.add('rtu-fan-active');
  } else {
    sfText.textContent = 'OFF';
    sfText.style.fill = '#7f8c8d';
    document.getElementById('supplyFan').querySelector('.rtu-fan-blade').classList.remove('rtu-fan-active');
  }

  // Update economizer
  document.getElementById('econStatus').textContent = economizerPosition.toFixed(0) + '%';
  var econAngle = (economizerPosition / 100) * 90;
  document.getElementById('oaDamper').setAttribute('transform', 'translate(92, 120) rotate(' + econAngle + ')');

  // Update compressors
  var comp1Circle = document.getElementById('compressor1').querySelector('circle');
  var comp1Light = document.getElementById('comp1Light');
  var comp1Text = document.getElementById('comp1Text');
  if (compressor1) {
    comp1Circle.classList.add('rtu-compressor-active');
    comp1Light.setAttribute('class', 'status-running');
    comp1Text.textContent = 'Running';
    comp1Text.style.fill = '#3498db';
  } else {
    comp1Circle.classList.remove('rtu-compressor-active');
    comp1Light.setAttribute('class', 'status-ok');
    comp1Text.textContent = 'Off';
    comp1Text.style.fill = '#7f8c8d';
  }

  var comp2Circle = document.getElementById('compressor2').querySelector('circle');
  var comp2Light = document.getElementById('comp2Light');
  var comp2Text = document.getElementById('comp2Text');
  if (compressor2) {
    comp2Circle.classList.add('rtu-compressor-active');
    comp2Light.setAttribute('class', 'status-running');
    comp2Text.textContent = 'Running';
    comp2Text.style.fill = '#3498db';
  } else {
    comp2Circle.classList.remove('rtu-compressor-active');
    comp2Light.setAttribute('class', 'status-ok');
    comp2Text.textContent = 'Off';
    comp2Text.style.fill = '#7f8c8d';
  }

  // Update DX coil appearance
  var dxCoil = document.getElementById('dxCoil');
  if (compressor1 || compressor2) {
    dxCoil.style.filter = 'drop-shadow(0 0 3px #3498db)';
  } else {
    dxCoil.style.filter = 'none';
  }

  // Update heating
  var heatCoil = document.getElementById('heatingCoil');
  var heatLight = document.getElementById('heatLight');
  var heatText = document.getElementById('heatText');
  if (heatingActive && heatingOutput > 5) {
    heatCoil.classList.add('rtu-coil-active');
    heatLight.setAttribute('class', 'status-running');
    heatText.textContent = heatingOutput.toFixed(0) + '%';
    heatText.style.fill = '#e74c3c';
  } else {
    heatCoil.classList.remove('rtu-coil-active');
    heatLight.setAttribute('class', 'status-ok');
    heatText.textContent = 'Off';
    heatText.style.fill = '#7f8c8d';
  }

  // Update stage display
  var stageText = '';
  if (coolingStage > 0) {
    stageText = 'Cool ' + coolingStage;
  } else if (heatingStage > 0) {
    stageText = 'Heat ' + heatingStage;
  } else {
    stageText = 'Off';
  }
  document.getElementById('stageStatus').textContent = stageText;

  // Update capacity
  var capacity = 0;
  if (compressor1 && compressor2) capacity = 100;
  else if (compressor1 || compressor2) capacity = 50;
  else if (heatingActive) capacity = heatingOutput;
  document.getElementById('capacityText').textContent = capacity.toFixed(0) + '%';

  // Update filter status
  var filterLight = document.getElementById('filterLight');
  var filterText = document.getElementById('filterText');
  if (filterDirty) {
    filterLight.setAttribute('class', 'status-alarm');
    filterText.textContent = 'Dirty';
    filterText.style.fill = '#e74c3c';
  } else {
    filterLight.setAttribute('class', 'status-ok');
    filterText.textContent = 'Clean';
    filterText.style.fill = '#27ae60';
  }

  // Update system status
  var statusLight = document.getElementById('statusLight');
  var statusText = document.getElementById('statusText');
  if (alarmActive) {
    statusLight.setAttribute('class', 'status-alarm');
    statusText.textContent = 'Alarm';
    statusText.style.fill = '#e74c3c';
  } else {
    statusLight.setAttribute('class', 'status-ok');
    statusText.textContent = 'Normal';
    statusText.style.fill = '#27ae60';
  }

  // Auto control logic for cooling stages
  compressor1 = coolingStage >= 1;
  compressor2 = coolingStage >= 2;
  heatingActive = heatingStage > 0;
  if (heatingStage === 1) heatingOutput = 50;
  else if (heatingStage === 2) heatingOutput = 100;
  else heatingOutput = 0;
}

// Initialize display
updateDisplay();

// Update display every 100ms
setInterval(updateDisplay, 100);
