        message: 'No active alarms',
        timestamp: '',
        acknowledged: false,
        scroll: true,
        scrollSpeed: 50, // pixels per second
        alarmCount: 0
      };

      // Priority definitions
      var priorities = {
        0: { name: 'INFO', class: 'banner-none', color: '#333' },
