  <script><![CDATA[
//!export-start
let _pn_alarmActive = 1;         // Alarm active: 0=inactive, 1=active
let _ps_alarmMessage = "High Temperature Alert"; // Alarm message
let _pn_priority = 2;            // Priority: 1=low, 2=medium, 3=high
let _ps_timestamp = "14:35:22";  // Timestamp
//!export-end

function init() { update(); }

function update() {
  const bannerElem = document.querySelector('.alarm-banner');
  const messageElem = document.getElementById('alarmMessage');
  const timestampElem = document.getElementById('timestamp');
  const priorityIcon = document.getElementById('priorityIcon');

  if (!bannerElem) return;

  if (_pn_alarmActive) {
    bannerElem.style.display = 'block';
    if (_pn_priority === 3) {
      bannerElem.setAttribute('fill', '#f56565');
    } else if (_pn_priority === 2) {
      bannerElem.setAttribute('fill', '#ed8936');
    } else {
      bannerElem.setAttribute('fill', '#ecc94b');
    }
  } else {
    bannerElem.style.display = 'none';
  }

  if (messageElem) messageElem.textContent = _ps_alarmMessage;
  if (timestampElem) timestampElem.textContent = _ps_timestamp;
}

function putValue(id, value) {
  if (id === '_pn_alarmActive') _pn_alarmActive = Number(value) || 0;
  else if (id === '_ps_alarmMessage') _ps_alarmMessage = String(value);
  else if (id === '_pn_priority') _pn_priority = Number(value) || 0;
  else if (id === '_ps_timestamp') _ps_timestamp = String(value);
  update();
}

init();
