svg.script:

var status = 0;            // 0=off, 1=on
var capacity = 0;          // 0-100 (%)
var outputKW = 0;          // kW output

var waveOffset = 0;
var animationId = null;

function updateWidget() {
  var body = document.getElementById('heaterBody');
  var elements = document.getElementById('elements');
  var statusText = document.getElementById('statusText');
  var outputText = document.getElementById('outputText');
  var heatWaves = document.getElementById('heatWaves');

  if (status === 1 && capacity > 0) {
    // Heating
    body.setAttribute('class', 'heater-body');
    var paths = elements.querySelectorAll('path');
    paths.forEach(function(path) {
      path.setAttribute('class', 'heating-element');
    });

    statusText.textContent = 'HEATING';
    outputText.textContent = Math.round(capacity) + '%';

    // Animate heat waves
    heatWaves.setAttribute('opacity', capacity / 100);
    if (!animationId) {
      animationId = setInterval(function() {
        waveOffset += 1;
        var waves = heatWaves.querySelectorAll('path');
        waves.forEach(function(wave, i) {
          var offset = (waveOffset + i * 10) % 30;
          wave.setAttribute('transform', 'translate(0, ' + (-offset) + ')');
          wave.setAttribute('opacity', 1 - offset / 30);
        });
      }, 100);
    }
  } else {
    // Off
    if (animationId) {
      clearInterval(animationId);
      animationId = null;
    }
    body.setAttribute('class', 'heater-off');
    var paths = elements.querySelectorAll('path');
    paths.forEach(function(path) {
      path.setAttribute('class', 'heating-element-off');
    });
    heatWaves.setAttribute('opacity', '0');
    statusText.textContent = 'OFF';
    outputText.textContent = '0%';
  }
}

updateWidget();
