svg.script:

var status = 0;        // 0=off, 1=on
var alarmState = 0;    // 0=normal, 1=alarm
var runCommand = 0;
var runFeedback = 0;

var rotation = 0;
var animationId = null;

function updateWidget() {
  var impeller = document.getElementById('impeller');
  var indicator = document.getElementById('statusIndicator');
  var statusText = document.getElementById('statusText');

  if (status === 1) {
    // Running
    if (!animationId) {
      animationId = setInterval(function() {
        rotation += 15;
        impeller.setAttribute('transform', 'rotate(' + rotation + ' 50 40)');
      }, 50);
    }
    indicator.setAttribute('class', 'status-on');
    statusText.textContent = 'RUN';
  } else {
    // Stopped
    if (animationId) {
      clearInterval(animationId);
      animationId = null;
    }
    indicator.setAttribute('class', 'status-off');
    statusText.textContent = 'OFF';
  }

  // Alarm state
  if (alarmState === 1) {
    indicator.setAttribute('class', 'status-alarm');
    statusText.textContent = 'ALARM';
  }
}

updateWidget();
