svg.script:

var temperature = 72.5;    // Current temperature
var units = 'F';           // 'F' or 'C'
var highAlarm = 80;        // High alarm setpoint
var lowAlarm = 60;         // Low alarm setpoint
var alarmState = 0;        // 0=normal, 1=alarm

function updateWidget() {
  var valueText = document.getElementById('valueText');
  var unitText = document.getElementById('unitText');
  var statusDot = document.getElementById('statusDot');

  // Update value
  valueText.textContent = temperature.toFixed(1);
  unitText.textContent = '°' + units;

  // Check alarm conditions
  if (temperature > highAlarm || temperature < lowAlarm || alarmState === 1) {
    statusDot.setAttribute('class', 'alarm-state');
    valueText.setAttribute('fill', '#e74c3c');
  } else {
    statusDot.setAttribute('class', 'normal-state');
    valueText.setAttribute('fill', '#2c3e50');
  }
}

updateWidget();
