svg.script:

var position = 0;  // 0=A→B, 50=A→B+C, 100=A→C

function updateWidget() {
  var ball = document.getElementById('valveBall');
  var posText = document.getElementById('positionText');
  var flowB = document.getElementById('flowB');
  var flowC = document.getElementById('flowC');

  // Rotate ball based on position (0° = A→B, 90° = A→C)
  var angle = (position / 100) * 90;
  ball.setAttribute('transform', 'rotate(' + angle + ' 50 50)');

  // Update flow indicators
  if (position < 25) {
    // Mostly to B
    flowB.setAttribute('opacity', '0.8');
    flowC.setAttribute('opacity', '0.1');
    posText.textContent = 'A→B';
  } else if (position > 75) {
    // Mostly to C
    flowB.setAttribute('opacity', '0.1');
    flowC.setAttribute('opacity', '0.8');
    posText.textContent = 'A→C';
  } else {
    // Mixed position
    var ratioB = 1 - (position / 100);
    var ratioC = position / 100;
    flowB.setAttribute('opacity', ratioB * 0.8);
    flowC.setAttribute('opacity', ratioC * 0.8);
    posText.textContent = 'MIX ' + Math.round(position) + '%';
  }
}

updateWidget();
