svg.script:

var position = 0;     // 0-100 (%)
var command = 0;      // Commanded position
var feedback = 0;     // Actual position feedback

function updateWidget() {
  var gaugeFill = document.getElementById('gaugeFill');
  var valueText = document.getElementById('valueText');
  var stemTop = document.getElementById('stemTop');

  // Use feedback if available, otherwise use command
  var displayPos = feedback > 0 ? feedback : position;
  displayPos = Math.max(0, Math.min(100, displayPos));

  // Update gauge fill (inverted - fills from bottom)
  var fillHeight = (displayPos / 100) * 50;
  gaugeFill.setAttribute('y', 70 - fillHeight);
  gaugeFill.setAttribute('height', fillHeight);

  // Update stem position
  var stemY = 27 + (25 * (1 - displayPos / 100));
  stemTop.setAttribute('cy', stemY);

  // Update value display
  valueText.textContent = Math.round(displayPos) + '%';

  // Color based on position
  if (displayPos < 10) {
    gaugeFill.setAttribute('fill', '#e74c3c');
  } else if (displayPos > 90) {
    gaugeFill.setAttribute('fill', '#27ae60');
  } else {
    gaugeFill.setAttribute('fill', '#3498db');
  }
}

updateWidget();
