svg.script:

// FUXA Integration Variables
var vrfSystem = {
  // ODU
  odu_status: true,
  comp1_status: true,
  comp1_speed: 90,
  comp2_status: true,
  comp2_speed: 85,
  odu_fan_status: true,
  odu_fan_speed: 88,
  inverter_freq: 75,
  outdoor_temp: 85.5,
  discharge_temp: 125.3,
  odu_power: 12.5,

  // IDU 1
  idu1_status: true,
  idu1_temp: 72.5,
  idu1_setpoint: 72.0,
  idu1_mode: "HEAT",
  idu1_capacity: 85,
  idu1_fan_speed: "MED",
  idu1_power: 2.8,

  // IDU 2
  idu2_status: true,
  idu2_temp: 70.8,
  idu2_setpoint: 71.0,
  idu2_mode: "HEAT",
  idu2_capacity: 92,
  idu2_fan_speed: "HIGH",
  idu2_power: 3.2,

  // IDU 3
  idu3_status: true,
  idu3_temp: 73.2,
  idu3_setpoint: 72.0,
  idu3_mode: "HEAT",
  idu3_capacity: 68,
  idu3_fan_speed: "LOW",
  idu3_power: 2.3,

  // IDU 4
  idu4_status: true,
  idu4_temp: 71.8,
  idu4_setpoint: 72.0,
  idu4_mode: "HEAT",
  idu4_capacity: 75,
  idu4_power: 2.5,

  // System Summary
  total_capacity: 20,
  active_idus: "4 of 5",
  system_load: 80,
  total_power: 13.3,
  system_cop: 4.2,
  system_mode: "HEATING",
  discharge_pressure: 420,
  suction_pressure: 135,
  communication: "NORMAL",
  system_status: "RUNNING"
};

function updateDisplay() {
  // Update ODU
  document.getElementById('odu-status').setAttribute('fill', vrfSystem.odu_status ? '#00ff00' : '#ff0000');
  document.getElementById('comp1-status').setAttribute('fill', vrfSystem.comp1_status ? '#00ff00' : '#ff0000');
  document.getElementById('comp1-speed').textContent = vrfSystem.comp1_speed + '%';
  document.getElementById('comp2-status').setAttribute('fill', vrfSystem.comp2_status ? '#00ff00' : '#ff0000');
  document.getElementById('comp2-speed').textContent = vrfSystem.comp2_speed + '%';
  document.getElementById('odu-fan-status').setAttribute('fill', vrfSystem.odu_fan_status ? '#00ff00' : '#ff0000');
  document.getElementById('odu-fan-speed').textContent = 'Fan: ' + vrfSystem.odu_fan_speed + '%';
  document.getElementById('inverter-freq').textContent = vrfSystem.inverter_freq + ' Hz';
  document.getElementById('outdoor-temp').textContent = vrfSystem.outdoor_temp.toFixed(1) + '°F';
  document.getElementById('discharge-temp').textContent = vrfSystem.discharge_temp.toFixed(1) + '°F';
  document.getElementById('odu-power').textContent = vrfSystem.odu_power.toFixed(1) + ' kW';

  // Update IDU 1
  document.getElementById('idu1-status').setAttribute('fill', vrfSystem.idu1_status ? '#00ff00' : '#ff0000');
  document.getElementById('idu1-temp').textContent = vrfSystem.idu1_temp.toFixed(1) + '°F';
  document.getElementById('idu1-setpoint').textContent = vrfSystem.idu1_setpoint.toFixed(1) + '°F';
  document.getElementById('idu1-mode').textContent = vrfSystem.idu1_mode;
  document.getElementById('idu1-capacity').textContent = vrfSystem.idu1_capacity + '%';
  document.getElementById('idu1-fan-speed').textContent = vrfSystem.idu1_fan_speed;
  document.getElementById('idu1-power').textContent = vrfSystem.idu1_power.toFixed(1) + ' kW';

  // Update IDU 2
  document.getElementById('idu2-status').setAttribute('fill', vrfSystem.idu2_status ? '#00ff00' : '#ff0000');
  document.getElementById('idu2-temp').textContent = vrfSystem.idu2_temp.toFixed(1) + '°F';
  document.getElementById('idu2-setpoint').textContent = vrfSystem.idu2_setpoint.toFixed(1) + '°F';
  document.getElementById('idu2-mode').textContent = vrfSystem.idu2_mode;
  document.getElementById('idu2-capacity').textContent = vrfSystem.idu2_capacity + '%';
  document.getElementById('idu2-fan-speed').textContent = vrfSystem.idu2_fan_speed;
  document.getElementById('idu2-power').textContent = vrfSystem.idu2_power.toFixed(1) + ' kW';

  // Update IDU 3
  document.getElementById('idu3-status').setAttribute('fill', vrfSystem.idu3_status ? '#00ff00' : '#ff0000');
  document.getElementById('idu3-temp').textContent = vrfSystem.idu3_temp.toFixed(1) + '°F';
  document.getElementById('idu3-setpoint').textContent = vrfSystem.idu3_setpoint.toFixed(1) + '°F';
  document.getElementById('idu3-mode').textContent = vrfSystem.idu3_mode;
  document.getElementById('idu3-capacity').textContent = vrfSystem.idu3_capacity + '%';
  document.getElementById('idu3-fan-speed').textContent = vrfSystem.idu3_fan_speed;
  document.getElementById('idu3-power').textContent = vrfSystem.idu3_power.toFixed(1) + ' kW';

  // Update IDU 4
  document.getElementById('idu4-status').setAttribute('fill', vrfSystem.idu4_status ? '#00ff00' : '#ff0000');
  document.getElementById('idu4-temp').textContent = vrfSystem.idu4_temp.toFixed(1) + '°F';
  document.getElementById('idu4-setpoint').textContent = vrfSystem.idu4_setpoint.toFixed(1) + '°F';
  document.getElementById('idu4-mode').textContent = vrfSystem.idu4_mode;
  document.getElementById('idu4-capacity').textContent = vrfSystem.idu4_capacity + '%';
  document.getElementById('idu4-power').textContent = vrfSystem.idu4_power.toFixed(1) + ' kW';

  // Update System Summary
  document.getElementById('total-capacity').textContent = vrfSystem.total_capacity + ' Tons';
  document.getElementById('active-idus').textContent = vrfSystem.active_idus;
  document.getElementById('system-load').textContent = vrfSystem.system_load + '%';
  document.getElementById('total-power').textContent = vrfSystem.total_power.toFixed(1) + ' kW';
  document.getElementById('system-cop').textContent = vrfSystem.system_cop.toFixed(1);
  document.getElementById('system-mode').textContent = vrfSystem.system_mode;
  document.getElementById('discharge-pressure').textContent = vrfSystem.discharge_pressure + ' PSI';
  document.getElementById('suction-pressure').textContent = vrfSystem.suction_pressure + ' PSI';
  document.getElementById('communication').textContent = vrfSystem.communication;
  document.getElementById('system-status').textContent = vrfSystem.system_status;

  var statusColor = vrfSystem.system_status === 'RUNNING' ? '#00ff00' :
                   vrfSystem.system_status === 'ALARM' ? '#ff0000' : '#ffaa00';
  document.getElementById('system-status').setAttribute('fill', statusColor);

  var commColor = vrfSystem.communication === 'NORMAL' ? '#00ff00' : '#ff0000';
  document.getElementById('communication').setAttribute('fill', commColor);
}

// Initialize display
updateDisplay();

// Update every second
setInterval(updateDisplay, 1000);
