svg.script:
//!export-start
let _ps_roomName = "Conference Room B";  // Room name
let _pn_status = 0;                       // Status: 0=Available, 1=Booked, 2=In Use
let _ps_bookingTitle = "Team Standup";   // Current booking title
let _ps_bookingTime = "2:00 PM - 3:00 PM"; // Booking time
let _ps_bookingOrganizer = "John Smith"; // Booking organizer
let _ps_nextBooking = "Budget Review - 3:30 PM"; // Next booking
let _pn_temperature = 72;                 // Temperature (°F)
let _pn_lightLevel = 75;                  // Light level (%)
let _pn_displayOn = 0;                    // Display on/off: 0=off, 1=on
let _pn_projectorOn = 0;                  // Projector on/off: 0=off, 1=on
let _pn_audioOn = 0;                      // Audio on/off: 0=off, 1=on
let _pn_videoOn = 0;                      // Video on/off: 0=off, 1=on
let _pn_occupancy = 6;                    // Number of people
let _ps_airQuality = "Good";              // Air quality status
let _pn_timeRemaining = 45;               // Time remaining (min)
//!export-end

function init() {
  update();
}

function update() {
  const roomNameElem = document.getElementById('roomName');
  const statusBadge = document.getElementById('statusBadge');
  const statusText = document.getElementById('statusText');
  const bookingTitleElem = document.getElementById('bookingTitle');
  const bookingTimeElem = document.getElementById('bookingTime');
  const bookingOrganizerElem = document.getElementById('bookingOrganizer');
  const nextBookingElem = document.getElementById('nextBooking');
  const confTempElem = document.getElementById('confTemp');
  const lightLevelElem = document.getElementById('lightLevel');
  const displayStatusElem = document.getElementById('displayStatus');
  const projectorStatusElem = document.getElementById('projectorStatus');
  const audioStatusElem = document.getElementById('audioStatus');
  const videoStatusElem = document.getElementById('videoStatus');
  const occupancyElem = document.getElementById('occupancy');
  const airQualityElem = document.getElementById('airQuality');
  const timeRemainingElem = document.getElementById('timeRemaining');

  if (!roomNameElem) return;

  // Update room name
  roomNameElem.textContent = _ps_roomName;

  // Update status
  const statusMap = {
    0: { text: 'Available', class: 'conf-available' },
    1: { text: 'Booked', class: 'conf-booked' },
    2: { text: 'In Use', class: 'conf-in-use' }
  };
  const statusInfo = statusMap[_pn_status] || statusMap[0];
  statusBadge.setAttribute('class', statusInfo.class);
  statusText.textContent = statusInfo.text;

  // Update booking information
  bookingTitleElem.textContent = _ps_bookingTitle;
  bookingTimeElem.textContent = _ps_bookingTime;
  bookingOrganizerElem.textContent = 'Organizer: ' + _ps_bookingOrganizer;
  nextBookingElem.textContent = _ps_nextBooking;

  // Update temperature
  confTempElem.textContent = Math.round(_pn_temperature) + '°F';

  // Update light level
  lightLevelElem.textContent = Math.round(_pn_lightLevel) + '%';

  // Update AV system status
  displayStatusElem.textContent = _pn_displayOn ? 'Display On' : 'Display Off';
  displayStatusElem.setAttribute('fill', _pn_displayOn ? '#48bb78' : '#ffffff');

  projectorStatusElem.textContent = _pn_projectorOn ? 'Projector On' : 'Projector Off';
  projectorStatusElem.setAttribute('fill', _pn_projectorOn ? '#48bb78' : '#ffffff');

  audioStatusElem.textContent = _pn_audioOn ? 'Audio On' : 'Audio Off';
  audioStatusElem.setAttribute('fill', _pn_audioOn ? '#48bb78' : '#ffffff');

  videoStatusElem.textContent = _pn_videoOn ? 'Video On' : 'Video Off';
  videoStatusElem.setAttribute('fill', _pn_videoOn ? '#48bb78' : '#ffffff');

  // Update room statistics
  occupancyElem.textContent = _pn_occupancy + ' people';
  airQualityElem.textContent = _ps_airQuality;
  timeRemainingElem.textContent = _pn_timeRemaining + ' min';
}

function putValue(id, value) {
  if (id === '_ps_roomName') {
    _ps_roomName = String(value);
  } else if (id === '_pn_status') {
    _pn_status = Number(value) || 0;
  } else if (id === '_ps_bookingTitle') {
    _ps_bookingTitle = String(value);
  } else if (id === '_ps_bookingTime') {
    _ps_bookingTime = String(value);
  } else if (id === '_ps_bookingOrganizer') {
    _ps_bookingOrganizer = String(value);
  } else if (id === '_ps_nextBooking') {
    _ps_nextBooking = String(value);
  } else if (id === '_pn_temperature') {
    _pn_temperature = Number(value) || 0;
  } else if (id === '_pn_lightLevel') {
    _pn_lightLevel = Number(value) || 0;
  } else if (id === '_pn_displayOn') {
    _pn_displayOn = Number(value) || 0;
  } else if (id === '_pn_projectorOn') {
    _pn_projectorOn = Number(value) || 0;
  } else if (id === '_pn_audioOn') {
    _pn_audioOn = Number(value) || 0;
  } else if (id === '_pn_videoOn') {
    _pn_videoOn = Number(value) || 0;
  } else if (id === '_pn_occupancy') {
    _pn_occupancy = Number(value) || 0;
  } else if (id === '_ps_airQuality') {
    _ps_airQuality = String(value);
  } else if (id === '_pn_timeRemaining') {
    _pn_timeRemaining = Number(value) || 0;
  }

  update();
}

init();
